(() => {
        const selectors = [
          'banner a[href="/"] img',
          'banner img',
          'header a[href="/"] img',
          'header img[alt*="logo" i]',
          'img[class*="logo" i]',
          'a[class*="logo" i] img',
          'header img:first-of-type',
          '.header img',
          '.logo img',
          'nav img:first-of-type'
        ];

        for (const selector of selectors) {
          const logo = document.querySelector(selector);
          if (logo && logo.src && !logo.src.includes('data:image')) {
            return {
              found: true,
              src: logo.src,
              currentSrc: logo.currentSrc,
              alt: logo.alt,
              width: logo.width,
              height: logo.height,
              selector: selector
            };
          }
        }
        return { found: false };
      })()

